attrs.script:
#get auth token

url = "https://@@{address}@@/auth/login"

payload = {'username':'admin','password':'@@{UCP_PASSWORD}@@'}
headers = {
    'Content-Type': "application/json",
    }

r = urlreq(url, verb="POST", params=json.dumps(payload), headers=headers, verify=False)

if r.ok:
    response = json.loads(r.content)
    auth_token=response['auth_token']
    print 'auth_token=%s' % auth_token
else:
    print "Get auth token request failed ", r.content
    exit(1)

#set roles

url = "https://@@{address}@@/roles"

headers = {
    'Content-Type': "application/json",
    'Accept': 'application/json',
    'Authorization': 'Bearer ' + auth_token
}

payload = """{
  "name": "nfs-client-provisioner-runner",
  "system_role": false,
  "operations": {
    "Kubernetes Persistent Volume Claim": {
      "PersistentVolumeClaim Get": [],
      "PersistentVolumeClaim List": [],
      "PersistentVolumeClaim Watch": [],
      "PersistentVolumeClaim Update": []
    },
    "Kubernetes Persistent Volume": {
      "PersistentVolume Get": [],
      "PersistentVolume List": [],
      "PersistentVolume Watch": [],
      "PersistentVolume Create": [],
      "PersistentVolume Delete": []
    },
    "Kubernetes Storage Class": {
      "StorageClass Get": [],
      "StorageClass List": [],
      "StorageClass Watch": []
    },
    "Kubernetes Event": {
      "Event List": [],
      "Event Watch": [],
      "Event Create": [],
      "Event Update": [],
      "Event Patch": []
    }
  }
}"""



r = urlreq(url, verb="POST", params=payload, headers=headers, verify=False)

if r.ok:
    response = json.loads(r.content)
    roles_id=response['id']
    print 'role_id=%s' % roles_id
else:
    print "Set roles request failed", r.content
    exit(1)


# create service serviceaccount

url = "https://@@{address}@@/api/v1/namespaces/default/serviceaccounts"

payload = """{
	"apiVersion": "v1",
	"kind": "ServiceAccount",
	"metadata": {
		"name": "nfs-client-provisioner"
	}
}"""

r = urlreq(url, verb="POST", params=payload, headers=headers, verify=False)

if r.ok:
    print r.content
else:
    print "Set serviceaccount request failed ", r.content
    exit(1)


# create grant

url = "https://@@{address}@@/collectionGrants/system:serviceaccount:default:nfs-client-provisioner/kubernetesnamespaces/%s?type=grantobject" % roles_id
payload = "{}"

r = urlreq(url, verb="PUT", params=payload, headers=headers, verify=False)

if r.ok:
    print r.content
else:
    print "Set grant request failed ", r.content
    exit(1)


# create nfs-client-provisioner deployment

url = "https://@@{address}@@/apis/extensions/v1beta1/namespaces/default/deployments"

payload = """{
	"kind": "Deployment",
	"apiVersion": "extensions/v1beta1",
	"metadata": {
		"name": "nfs-client-provisioner"
	},
	"spec": {
		"replicas": 1,
		"strategy": {
			"type": "Recreate"
		},
		"template": {
			"metadata": {
				"labels": {
					"app": "nfs-client-provisioner"
				}
			},
			"spec": {
				"serviceAccountName": "nfs-client-provisioner",
				"containers": [
					{
						"name": "nfs-client-provisioner",
						"image": "quay.io/external_storage/nfs-client-provisioner:latest",
						"volumeMounts": [
							{
								"name": "nfs-client-root",
								"mountPath": "/persistentvolumes"
							}
						],
						"env": [
							{
								"name": "PROVISIONER_NAME",
								"value": "afs-nxlab/k8s"
							},
							{
								"name": "NFS_SERVER",
								"value": "@@{AFS_HOST}@@"
							},
							{
								"name": "NFS_PATH",
								"value": "@@{AFS_SHARE}@@"
							}
						]
					}
				],
				"volumes": [
					{
						"name": "nfs-client-root",
						"nfs": {
							"server": "@@{AFS_HOST}@@",
							"path": "@@{AFS_SHARE}@@"
						}
					}
				]
			}
		}
	}
}"""

r = urlreq(url, verb="POST", params=payload, headers=headers, verify=False)

if r.ok:
    print r.content
else:
  print "Set nfs deployment request failed", r.content
  exit(1)

